import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Home from './Home';
import './App.css';
import netlifyIdentity from 'netlify-identity-widget';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const initNetlifyIdentity = () => {
      netlifyIdentity.init({
        container: '#netlify-modal', // Ensure this element exists in your HTML
        locale: 'en', // Optional: Specify the locale
      });

      netlifyIdentity.on('init', (user) => {
        setIsLoggedIn(!!user); // Update state based on user login status
      });

      netlifyIdentity.on('login', (user) => {
        setIsLoggedIn(true); // Update state to reflect login
        netlifyIdentity.close();
        navigate('/home'); // Redirect to home after login
      });

      netlifyIdentity.on('logout', () => {
        setIsLoggedIn(false); // Update state to reflect logout
        navigate('/'); // Redirect to login page after logout
      });
    };

    initNetlifyIdentity();

    return () => {
      netlifyIdentity.off('login');
      netlifyIdentity.off('logout');
      netlifyIdentity.off('init');
    };
  }, [navigate]);

  const handleSignupClick = (event) => {
    event.preventDefault();
    netlifyIdentity.open('signup');
  };

  const handleLoginClick = (event) => {
    event.preventDefault();
    netlifyIdentity.open('login');
  };

  const handleLogoutClick = () => {
    netlifyIdentity.logout();
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {isLoggedIn && <button onClick={handleLogoutClick} className="App-button">Logout</button>}
        </header>
        {!isLoggedIn ? (
          <div className="App-buttons">
            <button onClick={handleLoginClick} className="App-button">Login</button>
            <button onClick={handleSignupClick} className="App-button">Signup</button>
          </div>
        ) : (
          <Routes>
            <Route path="/" element={<Navigate to="/home" />} />
            <Route path="/home" element={<Home />} />
          </Routes>
        )}
        <div id="netlify-modal"></div>
      </div>
    </Router>
  );
}

export default App;
