import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './Home';
import './App.css';
import netlifyIdentity from 'netlify-identity-widget';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const initNetlifyIdentity = () => {
      netlifyIdentity.init({
        container: '#netlify-modal', // Ensure this element exists in your HTML
        locale: 'en', // Optional: Specify the locale
      });

      netlifyIdentity.on('init', (user) => {
        console.log('Netlify Identity user:', user);
        setIsLoggedIn(!!user); // Update state based on user login status
      });

      netlifyIdentity.on('login', (user) => {
        console.log('Logged in user:', user);
        setIsLoggedIn(true);
      });

      netlifyIdentity.on('logout', () => {
        console.log('Logged out');
        setIsLoggedIn(false); // Update state to reflect logout
      });

      // Event listeners for signup and login buttons
      const signupButton = document.getElementById('signup-button');
      const loginButton = document.getElementById('login-button');

      if (signupButton) {
        signupButton.addEventListener('click', (event) => {
          event.preventDefault();
          netlifyIdentity.open('signup');
        });
      }

      if (loginButton) {
        loginButton.addEventListener('click', (event) => {
          event.preventDefault();
          netlifyIdentity.open('login');
        });
      }
    };

    initNetlifyIdentity();

    // Cleanup function
    return () => {
      netlifyIdentity.off('login');
      netlifyIdentity.off('init');
    };
  }, []);

  const logout = () => {
    netlifyIdentity.logout(); // Log out the user
    setIsLoggedIn(false); // Update state to reflect logout
  };

  // PrivateRoute logic using Navigate component
  const PrivateRoute = ({ element, ...rest }) => {
    if (isLoggedIn) {
      return <Route {...rest} element={element} />;
    } else {
      return <Navigate to="/" replace />;
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {/* Header content if needed */}
          {isLoggedIn && <button onClick={logout} className="App-button">Logout</button>}
        </header>
        {!isLoggedIn && (
          <div className="App-buttons">
            <button id="login-button" className="App-button">Login</button>
            <button id="signup-button" className="App-button">Signup</button>
          </div>
        )}
        <Routes>
          <Route path="/" element={<Navigate to="/Home" />} />
          <PrivateRoute path="/Home" element={<Home />} />
        </Routes>
        {/* Render the Netlify Identity modal */}
        <div id="netlify-modal"></div>
      </div>
    </Router>
  );
}

export default App;
