import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';
import { FaHome, FaChartBar, FaTasks, FaUsers, FaWrench } from 'react-icons/fa';
import { Link } from 'react-router-dom';

function Home() {
  const [farms, setFarms] = useState([]);

  useEffect(() => {
    const fetchFarms = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/farms');
        setFarms(response.data);
      } catch (error) {
        console.error('Error fetching farms!', error);
      }
    };

    fetchFarms();
  }, []);

  return (
    <div className="home-container">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Farm Manager</h2>
        </div>
        <nav className="sidebar-nav">
          <Link to="/home" className="sidebar-button">
            <FaHome className="sidebar-icon" />
            Home
          </Link>
          <Link to="/reports" className="sidebar-button">
            <FaChartBar className="sidebar-icon" />
            Reports
          </Link>
          <Link to="/tasks" className="sidebar-button">
            <FaTasks className="sidebar-icon" />
            Tasks
          </Link>
          <Link to="/users" className="sidebar-button">
            <FaUsers className="sidebar-icon" />
            Users
          </Link>
          <Link to="/actions" className="sidebar-button">
            <FaWrench className="sidebar-icon" />
            Actions
          </Link>
        </nav>
      </div>
      <div className="main-content">
        <div className="content-section">
          <h3>Farm List</h3>
          <div className="actions-report">
            {farms.map(farm => (
              <div key={farm._id}>
                <p><strong>Name:</strong> {farm.name}</p>
                <p><strong>Location:</strong> {farm.location}</p>
                <p><strong>Area:</strong> {farm.area} acres</p>
                <p><strong>Chickens:</strong> {farm.chickens}</p>
                <hr />
              </div>
            ))}
          </div>
        </div>
        <div className="content-section">
          <h3>Other Information</h3>
          {/* Add more sections as needed */}
        </div>
      </div>
    </div>
  );
}

export default Home;
